import React, { useState, useEffect } from 'react';
import './Home.css';
import { FaArrowLeft, FaArrowRight } from 'react-icons/fa';
import cafeImage from '../assets/cafe.jpeg';
import coffeeImage from '../assets/coffee.jpeg';
import bakeryImage from '../assets/bakery.jpeg';
import foodImage from '../assets/food.jpeg';
import peopleImage from '../assets/people.jpeg';

const Home = () => {
  const images = [
    {
      src: cafeImage,
      alt: "Cafe Front",
      text: "A warm welcome to our cafe!",
      styleClass: "cafe-text",
    },
    {
      src: coffeeImage,
      alt: "Coffee Cup",
      text: "Freshly brewed coffee every day.",
      styleClass: "coffee-text",
    },
    {
      src: bakeryImage,
      alt: "Bakery Items",
      text: "Delicious baked goods made in-house.",
      styleClass: "bakery-text",
    },
    {
      src: foodImage,
      alt: "Delicious Food",
      text: "Savor our signature dishes.",
      styleClass: "food-text",
    },
    {
      src: peopleImage,
      alt: "People Around",
      text: "A perfect place to connect with friends.",
      styleClass: "people-text",
    },
  ];

  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [showScrollTop, setShowScrollTop] = useState(false);

  // Scroll-to-top logic
  useEffect(() => {
    const handleScroll = () => {
      setShowScrollTop(window.scrollY > 300);
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const handleNext = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === images.length - 1 ? 0 : prevIndex + 1
    );
  };

  const handlePrev = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  return (
    <div id="home" className="home">
      {/* Content Section */}
      <div className="home-content">
        <h1>Welcome to Food Hall Cafe</h1>
        <p>Experience the perfect blend of comfort, taste, and ambiance. Join us for an unforgettable coffee experience!</p>
      </div>
      <hr className="decorative-divider" />

      {/* Carousel Section */}
      <div className="carousel">
        <button className="carousel-btn prev-btn" onClick={handlePrev}>
          <FaArrowLeft />
        </button>
        <div className="carousel-slide">
          <img
            src={images[currentImageIndex].src}
            alt={images[currentImageIndex].alt}
            className="carousel-image"
          />
          <div className={`image-text ${images[currentImageIndex].styleClass}`}>
            {images[currentImageIndex].text}
          </div>
        </div>
        <button className="carousel-btn next-btn" onClick={handleNext}>
          <FaArrowRight />
        </button>
      </div>

      {/* Scroll-to-Top Button */}
      {showScrollTop && (
        <button className="scroll-top-btn" onClick={scrollToTop}>
          &#8593;
        </button>
      )}
    </div>
  );
};

export default Home;
