import React from 'react';
import './Home.css';
import cafeImage from '../assets/cafe.jpeg';
import coffeeImage from '../assets/coffee.jpeg';
import bakeryImage from '../assets/bakery.jpeg';
import foodImage from '../assets/food.jpeg';
import peopleImage from '../assets/people.jpeg';

const Home = () => {
  return (
    <div id="home" className="home">
      {/* Content Section */}
      <div className="home-content">
        <h1>Welcome to Food Hall Cafe</h1>
        <p>Experience the perfect blend of comfort, taste, and ambiance. Join us for an unforgettable coffee experience!</p>
      </div>

      {/* Image Grid Section */}
      <div className="image-grid">
        <img src={cafeImage} alt="Cafe Front" className="grid-image image-one" />
        <img src={coffeeImage} alt="Coffee Cup" className="grid-image image-two" />
        <img src={bakeryImage} alt="Bakery Items" className="grid-image image-three" />
        <img src={foodImage} alt="Delicious Food" className="grid-image image-" />
        <img src={peopleImage} alt="People Around" className="grid-image random-image" />
      </div>
    </div>
  );
};

export default Home;
